module.exports = Negotiator;
Negotiator.Negotiator = Negotiator;

function Negotiator(request) {
  if (!(this instanceof Negotiator)) return new Negotiator(request);
  this.request = request;
}

var set = { preferredCharset: [require('negotiator/lib/charset.js'), 'accept-charset'],
            preferredEncoding: [require('negotiator/lib/encoding.js'), 'accept-encoding'],
            preferredLanguage: [require('negotiator/lib/language.js'), 'accept-language'],
            preferredMediaType: [require('negotiator/lib/mediaType.js'), 'accept'] };

Object.keys(set).forEach(function (k) {
  var mh = set[k],
      method = mh[0],
      header = mh[1],
      singular = k,
      plural = k + 's';

  Negotiator.prototype[plural] = function (available) {
    return method(this.request.headers[header], available);
  };

  Negotiator.prototype[singular] = function(available) {
    var set = this[plural](available);
    if (set) return set[0];
  };
})
